using System;
using Microsoft.Office.Interop.Word;
using System.Reflection;
using System.Diagnostics;
using System.Windows.Forms;

public static class Snippet
{
	public static void func(System.String[] p_ArrayDocumentsToMerge, System.String p_MergedDocumentPath, System.String p_DocumentFontName, System.Boolean p_pageBreak)
	{

        Merge(p_ArrayDocumentsToMerge, p_MergedDocumentPath, p_pageBreak,p_DocumentFontName);	

	}
	public static void Merge(String[] filesToMerge, string fileSavePath, bool insertPageBreaks,string DocumentFontName)
        {
            object missing = System.Type.Missing;
            object pageBreak = WdBreakType.wdPageBreak;
            object outputFile = fileSavePath;


            Microsoft.Office.Interop.Word.Application wordApplication = new Microsoft.Office.Interop.Word.Application();
            try
            {
                Document wordDocument = wordApplication.Documents.Add(ref missing, ref missing, ref missing, ref missing);
                Selection selection = wordApplication.Selection;

                foreach (string docFile in filesToMerge)
                {
                    selection.InsertFile(docFile, ref missing, ref missing, ref missing, ref missing);
                    if (insertPageBreaks)
                    {
                        selection.InsertBreak(ref pageBreak);
                    }
                }
                var start = wordDocument.Content.Start;
                var end = wordDocument.Content.End;

                var docRange = wordDocument.Range(start, end);
                docRange.Select();


                selection.Font.Name = DocumentFontName;
                wordDocument.SaveAs(ref outputFile, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing);
                wordDocument = null;
                ((_Application)wordApplication).Quit(); 
            }
            catch (Exception ex) { throw ex; }
        }
}

using System;
using Microsoft.Office.Interop.Word;
using System.Reflection;
using System.Diagnostics;
using System.Windows.Forms;
using System.IO;	
using PdfSharp.Pdf;
using PdfSharp.Pdf.IO; 

public static class Snippet
{
	public static System.Data.DataTable func(System.String[] p_ArrayDocumentsToMerge, System.String p_MergedDocumentPath, System.String p_DocumentFontName, System.Boolean p_pageBreak, System.Data.DataTable p_ParticipantsDT)
	{

		Microsoft.Office.Interop.Word.Application wordApplication = new Microsoft.Office.Interop.Word.Application();
		System.Data.DataTable ParticipantsFieldsToSign = new System.Data.DataTable();
		wordApplication.Options.ConfirmConversions = false;

        ParticipantsFieldsToSign = MergeToPDF(wordApplication, p_ArrayDocumentsToMerge, p_MergedDocumentPath, p_pageBreak,p_DocumentFontName,p_ParticipantsDT);	
		return ParticipantsFieldsToSign;

	}

	public static System.Data.DataTable MergeToPDF(Microsoft.Office.Interop.Word.Application p_wordApplication,String[] filesToMerge, string fileSavePath, bool insertPageBreaks,string DocumentFontName,System.Data.DataTable p_ParticipantsDT)
    { 

            object missing = System.Type.Missing;
            object outputFile = fileSavePath;
            PdfDocument outputPDFDocument = new PdfDocument();
            PdfDocument inputPDFDocument = new PdfDocument();
			System.Data.DataTable x = new System.Data.DataTable();
			System.Data.DataTable AllMergeFieldsDT = new System.Data.DataTable();
            int DocumentCounter =0;
            try
            {
                p_wordApplication.Visible= false;

				//Go over each documents on the documents array
                foreach (string docFile in filesToMerge)
                {
					DocumentCounter++;
				    string pdfFile = Path.ChangeExtension(docFile,"pdf");
					//MessageBox.Show(pdfFile);
                	Document wordDocument = p_wordApplication.Documents.Open(docFile);

					x = MergeSignTags(p_ParticipantsDT,wordDocument,p_wordApplication,DocumentCounter);


					AllMergeFieldsDT.Merge(x, false, System.Data.MissingSchemaAction.Add);


					wordDocument.ExportAsFixedFormat(pdfFile, WdExportFormat.wdExportFormatPDF);
					((Microsoft.Office.Interop.Word._Document)wordDocument).Close(-1, ref missing, ref missing); 
					wordDocument = null;

						try
						{
							inputPDFDocument = PdfReader.Open(pdfFile, PdfDocumentOpenMode.Import);
						}
						catch(PdfSharp.Pdf.IO.PdfReaderException)
						{ 
							//string newName =  WriteCompatiblePdf(pdfFile);
							//inputPDFDocument = PdfReader.Open(newName, PdfDocumentOpenMode.Import);
						}

					outputPDFDocument.Version = inputPDFDocument.Version;

						foreach (PdfPage page in inputPDFDocument.Pages)
						{

							outputPDFDocument.AddPage(page);
						}

                }
				outputPDFDocument.Save(fileSavePath);


                ((_Application)p_wordApplication).Quit();

			 return AllMergeFieldsDT;	
            }
            catch (Exception ex) { 
				 ((_Application)p_wordApplication).Quit();
				MessageBox.Show("Err: "+ex.Message);
				throw ex; }

    }
	//merge all tags in the documents
	public static System.Data.DataTable MergeSignTags(System.Data.DataTable p_ParticipantsDT,Microsoft.Office.Interop.Word.Document p_wordDocument,Microsoft.Office.Interop.Word.Application p_wordApplication, int p_DocumentCounter)
	{
			//Create Datatable with particioates and all merge fields  
			System.Data.DataTable ParticipantsFieldsToSignDT = CreateParticipantsFieldsToSignDT();
			System.Data.DataRow myDataRow;
			object missing = System.Type.Missing;
			int NoOfDescriptionFiledsForPerticipate = 0;			

			for (int participateNo = 0; participateNo < p_ParticipantsDT.Rows.Count; participateNo++)
            {

				int NoOfMergeFiledsForPerticipate = 1;     	

				Range rng = p_wordDocument.Content;


                string id = p_ParticipantsDT.Rows[participateNo]["id"].ToString();
                string MergeFieldName = p_ParticipantsDT.Rows[participateNo]["MergeFieldName"].ToString();
                string TagTemplate = p_ParticipantsDT.Rows[participateNo]["TagTemplate"].ToString();
				string FieldType = p_ParticipantsDT.Rows[participateNo]["FieldType"].ToString(); 
				string FieldDescription = p_ParticipantsDT.Rows[participateNo]["FieldDescription"].ToString();

				//MessageBox.Show("MergeSignTags - FieldType:"+FieldType);
				//MessageBox.Show("MergeSignTags - MergeFieldName:"+MergeFieldName + "To Participate: "+participateNo.ToString());

                string MergedTag = TagTemplate;
				rng.Find.ClearFormatting();
				rng.Find.Forward = true;
				//MessageBox.Show(rng.Find.Wrap.ToString());
				rng.Find.Wrap = WdFindWrap.wdFindStop;
				rng.Find.Text = TagTemplate;

            rng.Find.Execute(
                ref missing, ref missing, ref missing, ref missing, ref missing,
                ref missing, ref missing, ref missing, ref missing, ref missing,
                ref missing, ref missing, ref missing, ref missing, ref missing);

            while (rng.Find.Found)
            {

                //NoOfMergeFiledsForPerticipate++;
				//MessageBox.Show("MergeSignTags - NoOfMergeFiledsForPerticipate: "+NoOfMergeFiledsForPerticipate.ToString());
				MergedTag = TagTemplate;
				MergedTag = MergedTag.Replace("(n)", "_"+NoOfMergeFiledsForPerticipate.ToString()+"_"+p_DocumentCounter.ToString()).Replace("(TZ)", id);
				//Add Field Row
				myDataRow = ParticipantsFieldsToSignDT.NewRow();
                myDataRow["id"] = id;
                myDataRow["FieldName"] = MergedTag;
				myDataRow["FieldType"]=FieldType;
				if (FieldDescription!="")
				{
					NoOfDescriptionFiledsForPerticipate++;
					FieldDescription = p_ParticipantsDT.Rows[participateNo]["FieldDescription"].ToString();
					FieldDescription = FieldDescription.Replace("(n)", "_"+NoOfDescriptionFiledsForPerticipate.ToString()); 				
					myDataRow["FieldDescription"]=FieldDescription;
				}

                //myDataRow["FieldType"] = "{Sign<n>_<TZ>}";
				ParticipantsFieldsToSignDT.Rows.Add(myDataRow);


                rng.Find.Execute(
                ref missing, ref missing, ref missing, ref missing, ref missing,
                ref missing, ref missing, ref missing, ref missing, ref missing,
                ref missing, ref missing, ref missing, ref missing, ref missing);
				//*** Replace all MegeFields with tag
				//MessageBox.Show("MergeSignTags;Call FindAndReplace Parameters:TagTemplate - "+ TagTemplate+" MergedTag - "+MergedTag);
				FindAndReplace(p_wordApplication,TagTemplate,MergedTag,ref NoOfMergeFiledsForPerticipate);



            }


                          //  MergedTag = MergedTag.Replace("(n)", NoOfMergeFiledsForPerticipate.ToString()).Replace("(TZ)", id.ToString());
                        //    FindAndReplace(p_wordApplication,TagTemplate,MergedTag);
          }
			//MessageBox.Show("ParticipantsFieldsToSignDT Rows: "+ParticipantsFieldsToSignDT.Rows.Count.ToString());
			return ParticipantsFieldsToSignDT;
	}
	public static void FindAndReplace(Microsoft.Office.Interop.Word.Application p_wordApplication,object findText, object replaceWithText,ref int p_NoOfMergeFiledsForPerticipate)
    {

			//MessageBox.Show("No of Show: "+p_NoOfMergeFiledsForPerticipate.ToString());
			//MessageBox.Show("replaceWithText: "+replaceWithText);
            //options
            object matchCase = false;
            object matchWholeWord = true;
            object matchWildCards = false;
            object matchSoundsLike = false;
            object matchAllWordForms = false;
            object forward = true;
            object format = false;
            object matchKashida = false;
            object matchDiacritics = false;
            object matchAlefHamza = false;
            object matchControl = false;
            object read_only = false;
            object visible = true;
            object replaceAll  = 1;
            object wrap = WdFindWrap.wdFindContinue;
            //execute find and replace
            p_wordApplication.Selection.Find.Execute(ref findText, ref matchCase, ref matchWholeWord,
                ref matchWildCards, ref matchSoundsLike, ref matchAllWordForms, ref forward, ref wrap, ref format, ref replaceWithText, ref replaceAll,
                ref matchKashida, ref matchDiacritics, ref matchAlefHamza, ref matchControl);

			//MessageBox.Show("Find and replace: " +findText + "with: "+ replaceWithText);
			p_NoOfMergeFiledsForPerticipate++;


    }
	public static System.Data.DataTable CreateParticipantsFieldsToSignDT()
	{
			try{
			System.Data.DataTable ParticipantsFieldsToSign = new System.Data.DataTable("ParticipantsFieldsToSign"); 
		    System.Data.DataColumn dtColumn;

			// Create id column  
            dtColumn = new System.Data.DataColumn();
            dtColumn.DataType = typeof(String);
            dtColumn.ColumnName = "id";
            // Add column to the DataColumnCollection.  
            ParticipantsFieldsToSign.Columns.Add(dtColumn);

            // Create FieldName column.    
            dtColumn = new System.Data.DataColumn();
            dtColumn.DataType = typeof(String);
            dtColumn.ColumnName = "FieldName";
            /// Add column to the DataColumnCollection.  
            ParticipantsFieldsToSign.Columns.Add(dtColumn);
			 // Create Type column.  
			dtColumn = new System.Data.DataColumn();
            dtColumn.DataType = typeof(String);
            dtColumn.ColumnName = "FieldType";
            /// Add column to the DataColumnCollection.  
            ParticipantsFieldsToSign.Columns.Add(dtColumn);
			// Create IsMandatory column.  
			dtColumn = new System.Data.DataColumn();
            dtColumn.DataType = typeof(bool);
            dtColumn.ColumnName = "IsMandatory";
            /// Add column to the DataColumnCollection.  
            ParticipantsFieldsToSign.Columns.Add(dtColumn);

			// Create FieldDescription column  
            dtColumn = new System.Data.DataColumn();
            dtColumn.DataType = typeof(String);
            dtColumn.ColumnName = "FieldDescription";
            // Add column to the DataColumnCollection.  
            ParticipantsFieldsToSign.Columns.Add(dtColumn);

				return ParticipantsFieldsToSign;
			}
			catch (Exception ex) { 
				MessageBox.Show("Error creating colums");
				throw ex; }

	}
}
