using System;
using Microsoft.Office.Interop.Word;
using System.Reflection;
using System.Diagnostics;
using System.Windows.Forms;

public static class Snippet
{
	public static void func(System.String[] p_ArrayDocumentsToMerge, System.String p_MergedDocumentPath, System.String p_DocumentFontName, System.Boolean p_pageBreak)
	{

        Merge(p_ArrayDocumentsToMerge, p_MergedDocumentPath, p_pageBreak,p_DocumentFontName);	

	}
	public static void Merge(String[] filesToMerge, string fileSavePath, bool insertPageBreaks,string DocumentFontName)
        {
            object missing = System.Type.Missing;
            object pageBreak = WdBreakType.wdPageBreak;
            object outputFile = fileSavePath;


            Microsoft.Office.Interop.Word.Application wordApplication = new Microsoft.Office.Interop.Word.Application();
            try
            {
                Document wordDocument = wordApplication.Documents.Add(ref missing, ref missing, ref missing, ref missing);
                Selection selection = wordApplication.Selection;

                foreach (string docFile in filesToMerge)
                {
                    selection.InsertFile(docFile, ref missing, ref missing, ref missing, ref missing);
                    if (insertPageBreaks)
                    {
                        selection.InsertBreak(ref pageBreak);
                    }
                }
                var start = wordDocument.Content.Start;
                var end = wordDocument.Content.End;

                var docRange = wordDocument.Range(start, end);
                docRange.Select();


                selection.Font.Name = DocumentFontName;
                wordDocument.SaveAs(ref outputFile, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing, ref missing);
                wordDocument = null;
                ((_Application)wordApplication).Quit(); 
            }
            catch (Exception ex) { throw ex; }
        }
}

using System;
using Microsoft.Office.Interop.Word;
using System.Reflection;
using System.Diagnostics;
using System.Windows.Forms;
using System.IO;
using PdfSharp.Pdf;
using PdfSharp.Pdf.IO; 

public static class Snippet
{
	public static void func(System.String[] p_ArrayDocumentsToMerge, System.String p_MergedDocumentPath, System.String p_DocumentFontName, System.Boolean p_pageBreak, System.Data.DataTable p_ParticipantsDT)
	{
		Microsoft.Office.Interop.Word.Application wordApplication = new Microsoft.Office.Interop.Word.Application();

        MergeToPDF(wordApplication, p_ArrayDocumentsToMerge, p_MergedDocumentPath, p_pageBreak,p_DocumentFontName,p_ParticipantsDT);	

	}
	public static void MergeToPDF(Microsoft.Office.Interop.Word.Application p_wordApplication,String[] filesToMerge, string fileSavePath, bool insertPageBreaks,string DocumentFontName,System.Data.DataTable p_ParticipantsDT )
        {
            object missing = System.Type.Missing;
            object outputFile = fileSavePath;
            PdfDocument outputPDFDocument = new PdfDocument();
            PdfDocument inputPDFDocument = new PdfDocument();


            try
            {
                p_wordApplication.Visible= false;


                foreach (string docFile in filesToMerge)
                {

				    string pdfFile = Path.ChangeExtension(docFile,"pdf");
					//MessageBox.Show(pdfFile);
                	Document wordDocument = p_wordApplication.Documents.Open(docFile);
					MergeSignTags(p_ParticipantsDT,wordDocument,p_wordApplication); 
					wordDocument.ExportAsFixedFormat(pdfFile, WdExportFormat.wdExportFormatPDF);
					((Microsoft.Office.Interop.Word._Document)wordDocument).Close(ref missing, ref missing, ref missing); 
					wordDocument = null;

						try
						{
							inputPDFDocument = PdfReader.Open(pdfFile, PdfDocumentOpenMode.Import);
						}
						catch(PdfSharp.Pdf.IO.PdfReaderException)
						{
							//string newName =  WriteCompatiblePdf(pdfFile);
							//inputPDFDocument = PdfReader.Open(newName, PdfDocumentOpenMode.Import);
						}

					outputPDFDocument.Version = inputPDFDocument.Version;

						foreach (PdfPage page in inputPDFDocument.Pages)
						{

							outputPDFDocument.AddPage(page);
						}

                }
				outputPDFDocument.Save(fileSavePath);


                ((_Application)p_wordApplication).Quit();


            }
            catch (Exception ex) { throw ex; }
        }
	public static void MergeSignTags(System.Data.DataTable p_ParticipantsDT,Microsoft.Office.Interop.Word.Document p_wordDocument,Microsoft.Office.Interop.Word.Application p_wordApplication)
		{
			for (int participateNo = 0; participateNo < p_ParticipantsDT.Rows.Count; participateNo++)
            {
                int NoOfMergeFiledsForPerticipate = 0;
                int id = (int)p_ParticipantsDT.Rows[participateNo][0];
                string MergeFieldName = p_ParticipantsDT.Rows[participateNo][1].ToString();
                string TagTemplate = p_ParticipantsDT.Rows[participateNo][2].ToString();
               // Console.WriteLine(id.ToString() + " , " + MergeFieldName + " , " + TagTemplate);
                foreach (Microsoft.Office.Interop.Word.Field MergeField in p_wordDocument.Fields) 
                {
                    Microsoft.Office.Interop.Word.Range rngFieldCode = MergeField.Code;
                    String fieldText = rngFieldCode.Text;
                    if (fieldText.StartsWith(" MERGEFIELD"))
                    {
                        Int32 endMerge = fieldText.IndexOf("\\");
                        Int32 fieldNameLength = fieldText.Length - endMerge;
                        String fieldName = fieldText.Substring(11, endMerge - 11);
                        fieldName = fieldName.Trim();

                        if (fieldName == MergeFieldName)
                        {
                            NoOfMergeFiledsForPerticipate++;
                            MergeField.Select();
                            string MergedTag = TagTemplate;
                            MergedTag = MergedTag.Replace("<n>", NoOfMergeFiledsForPerticipate.ToString()).Replace("<TZ>", id.ToString());
                            p_wordApplication.Selection.TypeText(MergedTag);
                        }
                    }
                }
            }
		}

}
