using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Data;
using System.Drawing;
using System.Text;
using System.Windows.Forms;
using PresentationControls;  

public static class Snippet
{
	public static void func(ref PresentationControls.CheckBoxComboBox cmbDataTableDataSource, System.Data.DataTable DTA)
	{


            cmbDataTableDataSource.DataSource = 
                new ListSelectionWrapper<DataRow>(
                    DTA.Rows,
                    "SomePropertyOrColumnName" // "SomePropertyOrColumnName" will populate the Name on ObjectSelectionWrapper.
                    ); 
            cmbDataTableDataSource.DisplayMemberSingleItem = "Name"; 
            cmbDataTableDataSource.DisplayMember = "NameConcatenated";
            cmbDataTableDataSource.ValueMember = "Selected";

	}

}

using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Data;
using System.Drawing;
using System.Text;
using System.Windows.Forms;
using PresentationControls;

public static class Snippet
{
	public static void func(ref PresentationControls.CheckBoxComboBox cmbDataTableDataSource)
	{
		DataTable DT = new DataTable("TEST TABLE FOR DEMO PURPOSES");
            DT.Columns.AddRange(
                new DataColumn[]
                {
                    new DataColumn("Id", typeof(int)),
                    new DataColumn("SomePropertyOrColumnName", typeof(string)),                    
                    new DataColumn("Description", typeof(string)),
                });
            DT.Rows.Add(1, "אאאא", "AAAAA");
            DT.Rows.Add(2, "בבבב", "BBBBB");
            DT.Rows.Add(3, "גגגג", "CCCCC");
            DT.Rows.Add(3, "דדדד", "DDDDD");

            cmbDataTableDataSource.DataSource = 
                new ListSelectionWrapper<DataRow>(
                    DT.Rows,
                    "SomePropertyOrColumnName" // "SomePropertyOrColumnName" will populate the Name on ObjectSelectionWrapper.
                    ); 
            cmbDataTableDataSource.DisplayMemberSingleItem = "Name"; 
            cmbDataTableDataSource.DisplayMember = "NameConcatenated";
            cmbDataTableDataSource.ValueMember = "Selected";


	}

}
